package com.jijizu.core.cache.service;

import java.io.Serializable;

/**
 ******************************************************************************* 
 * @project : 集集组
 * @type : CacheService
 * @function : 缓存操作业务接口。<br/>
 ******************************************************************************* 
 * @version ：1.1.0
 * @creator ：majun
 * @date ：2011-6-15
 ******************************************************************************* 
 * @revision ：
 * @revisor ：
 * @date ：
 * @memo ：
 ******************************************************************************* 
 */
public interface CacheService {

	/**
	 ******************************************************************************* 
	 * @function : 向缓存存放数据。<br/>
	 * @param key
	 *            数据的key标识
	 * @param data
	 *            数据
	 * @param expriation
	 *            过期时间，单位为秒
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：luke_huang
	 * @date ：2011-6-15
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	void setData(String key, Serializable data, int expriation)
			throws Exception;

	/**
	 ******************************************************************************* 
	 * @function : 删除缓存数据。<br/>
	 * @param key
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：luke_huang
	 * @date ：2011-6-15
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	void deleteData(String key) throws Exception;

	/**
	 ******************************************************************************* 
	 * @function : 从缓存取得数据。<br/>
	 * @param key
	 *            数据的key标识
	 * @return
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：luke_huang
	 * @date ：2011-6-15
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	Serializable getData(String key) throws Exception;

	/**
	 ******************************************************************************* 
	 * @function : 缓存数据加值。<br/>
	 *           如果数据不是数字会报错，请注意。<br/>
	 * @param key
	 *            数据的key标识
	 * @param incr
	 *            加值的大小
	 * @return
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：luke_huang
	 * @date ：2011-6-15
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	long incrData(String key, int incr) throws Exception;

	/**
	 ******************************************************************************* 
	 * @function : 缓存数据减值。<br/>
	 *           如果数据不是数字会报错，请注意。<br/>
	 * @param key
	 *            数据的key标识
	 * @param decr
	 *            减值的大小
	 * @return
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：luke_huang
	 * @date ：2011-6-15
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	long decrData(String key, int decr) throws Exception;

	/**
	 * 
	 ******************************************************************************* 
	 * @function : 向缓存存放SNA信息。<br/>
	 * @param key
	 *            SNA信息的key标识
	 * @param snaInfo
	 *            SNA信息
	 * @param expriation
	 * @throws Exception 
	 ******************************************************************************* 
	 * @creator ：nelson
	 * @date ：2011-6-16
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	void setSnaInfo(String key, Serializable snaInfo, int expriation) throws Exception;

	/**
	 * 
	 ******************************************************************************* 
	 * @function : 从缓存删除SNA信息。<br/>
	 * @param key
	 *            SNA信息的key标识
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：nelson
	 * @date ：2011-6-16
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	void deleteSnaInfo(String key) throws Exception;

	/**
	 * 
	 ******************************************************************************* 
	 * @function : 从缓存取得SNA信息。<br/>
	 * @param key
	 *            SNA信息的key标识
	 * @throws Exception
	 ******************************************************************************* 
	 * @creator ：nelson
	 * @date ：2011-6-16
	 ******************************************************************************* 
	 * @revisor ：
	 * @date ：
	 * @memo ：
	 ******************************************************************************* 
	 */
	Serializable getSnaInfo(String key) throws Exception;
}